mapper.select:
select u.id,u.name,u.user_name,u.type,u.created_at,u.created_name,u.modified_at,u.modified_name,u.compName,u.orgName,u.comp_id, u.org_id,u.parent_id_list,u.tel,u.state from v_s_user_role u,s_user_comm comm where u.id = comm.user_id and comm.comm_id= #{commId} and u.is_delete=0 and u.comp_id = #{compId}
and u.user_name like CONCAT('%',#{userName},'%')
and u.name like CONCAT('%',#{name},'%')
